import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // React Router
import { ThemeProvider } from '@mui/material/styles'; // Material-UI ThemeProvider
import CssBaseline from '@mui/material/CssBaseline'; // Material-UI Reset de Estilos Globais
import theme from './theme'; // Importando o Tema Customizado

// Importação das páginas e componentes
import Navbar from './pages/navbar/Navbar';
import Home from './componentes/Home/Home';
import Sobre from './componentes/Sobre/Sobre';
import Servicos from './componentes/Servicos/Servicos';
import Projetos from './componentes/Projetos/Projetos';
import Contato from './componentes/Contato/Contato';
import HomePortfolio from './componentes/Portfolio/HomePortfolio/HomePortfolio';
import CompetenciaPortfolio from './componentes/Portfolio/CompetenciaPortfolio/CompetenciaPortfolio';
import ProjetosPortfolio from './componentes/Portfolio/ProjetosPortfolio/ProjetosPortfolio';
import Hero from './pages/Hero/Hero'; // Importando o Hero Component (nova funcionalidade)

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline /> {/* Reset global do Material-UI */}
            <Router>
                {/* Menu de navegação fixo */}
                <Navbar />

                {/* Rotas Definidas */}
                <Routes>
                    <Route path="/home" element={<Home />} />
                    <Route path="/sobre" element={<Sobre />} />
                    <Route path="/servicos" element={<Servicos />} />
                    <Route path="/projetos" element={<Projetos />} />
                    <Route path="/contato" element={<Contato />} />

                    {/* Páginas do Portfólio */}
                    <Route path="/homePortfolio" element={<HomePortfolio />} />
                    <Route path="/competenciaPortfolio" element={<CompetenciaPortfolio />} />
                    <Route path="/projetosPortfolio" element={<ProjetosPortfolio />} />


                    {/* Adicionando o Hero como uma rota específica */}
                    <Route path="/hero" element={<Hero />} />
                </Routes>
            </Router>
        </ThemeProvider>
    );
}

export default App;



