import { Box, Container, Grid, Typography, styled } from "@mui/material";
import capaMo from "../../assets/capaMo.jpg";
import DownloadIcon from "@mui/icons-material/Download";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import StyledButton from "../../componentes/StyledButton/StyledButton";
import { AnimatedBackground } from "../AnimatedBackground";

const Home = () => {
    const StyledHero = styled("div")(({ theme }) => ({
        backgroundColor: theme.palette.primary.main,
        height: "100vh",
        display: "flex",
        alignItems: "center",
        [theme.breakpoints.up("xs")]: {
            paddingTop: "100px",
        },
        [theme.breakpoints.up("md")]: {
            paddingTop: "0",
        },
    }));

    const StyledImg = styled("img")(({ theme }) => ({
        width: "75%",
        borderRadius: "50%",
        border: `1px solid ${theme.palette.primary.contrastText}`,
    }));

    const HighlightText = styled(Typography)(({ theme }) => ({
        color: "#FFA652", // Cor laranja para destaque
        fontWeight: "bold",
        display: "inline",
    }));

    return (
        <>
            <StyledHero>
                <Container maxWidth="lg">
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={5}>
                            <Box position="relative">
                                <Box position="absolute" width={"150%"} top={-100} right={0}>
                                    <AnimatedBackground />
                                </Box>
                                <Box position="relative" textAlign="center">
                                    <StyledImg src={capaMo} />
                                </Box>
                            </Box>
                        </Grid>
                        <Grid item xs={12} md={7}>
                            <Typography
                                className="fade-in"
                                variant="h1"
                                textAlign="center"
                                pb={2}
                                color="primary.contrastText"
                            >
                                Sites e Sistemas
                            </Typography>
                            <Typography
                                className="fade-in delay-1"
                                variant="h4"
                                textAlign="center"
                                color="primary.contrastText"
                            >
                                Transformo suas <HighlightText>ideias</HighlightText> em{" "}
                                <HighlightText>realidade!</HighlightText>
                            </Typography>
                            <Typography
                                className="fade-in delay-2"
                                variant="h5"
                                textAlign="center"
                                color="primary.contrastText"
                            >
                                Crie seu site e sistema personalizado e conquiste mais{" "}
                                <HighlightText>clientes</HighlightText>!
                            </Typography>

                            <Grid container display="flex" justifyContent="center" spacing={3} pt={3}>
                                <Grid item xs={12} md={4} display="flex" justifyContent="center">
                                    <StyledButton>
                                        <DownloadIcon />
                                        <Typography>Orçamento grátis!</Typography>
                                    </StyledButton>
                                </Grid>
                                <Grid item xs={12} md={4} display="flex" justifyContent="center">
                                    <StyledButton>
                                        <MailOutlineIcon />
                                        <Typography>Entre em Contato!</Typography>
                                    </StyledButton>
                                </Grid>
                            </Grid>
                        </Grid>
                    </Grid>
                </Container>
            </StyledHero>
        </>
    );
};

export default Home;
