import React, { useState } from 'react';
import './Projetos.css';
import modalidade from "../../assets/modalidade.jpg.jpg";
import cadastroAluno from "../../assets/cadastroAluno.jpg.jpg";
import pagamento from "../../assets/pagamento.jpg.jpg";


const Projetos = () => {
    const [modalOpen, setModalOpen] = useState(false);
    const [selectedProject, setSelectedProject] = useState(null);

    const projetos = [
        {
            titulo: "Sistema de Gestão para Academias",
            descricao: "Sistema completo para academias, com funcionalidades de cadastro de alunos, controle de pagamentos, " +
                "agendamento de aulas, envio de notificações automáticas e geração de relatórios detalhados para otimizar a " +
                "gestão do seu negócio.",
            imagens: <img src={modalidade} alt="Modalidadee" className="modalidadeFoto" /> // Substitua pelos caminhos reais
        },
        {
            titulo: "Aplicativo de Gestão e Agendamento",
            descricao: "Aplicativo exclusivo com funcionalidades como cadastro de clientes, agendamento via WhatsApp, " +
                "notificações automáticas de atendimentos, e relatórios detalhados.",
            imagens: ["url_da_imagem1", "url_da_imagem2"] // Substitua pelos caminhos reais
        }

    ];

    const openModal = (projeto) => {
        setSelectedProject(projeto);
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
        setSelectedProject(null);
    };

    return (
        <section id="projetos" className="projetos">
            <h2 className="titulo-secao">Projetos Desenvolvidos</h2>
            <p className="descricao-secao">Confira alguns dos projetos que desenvolvi:</p>
            <div className="projetos-lista">
                {projetos.map((projeto, index) => (
                    <div key={index} className="projeto-card">
                        <h3 className="projeto-titulo">{projeto.titulo}</h3>
                        <p className="projeto-descricao">{projeto.descricao}</p>
                        <button
                            className="projeto-botao"
                            onClick={() => openModal(projeto)}
                        >
                            Ver Mais
                        </button>
                    </div>
                ))}
            </div>

            {modalOpen && selectedProject && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close-button" onClick={closeModal}>
                            &times;
                        </span>
                        <h3>{selectedProject.titulo}</h3>
                        <p>{selectedProject.descricao}</p>
                        <div className="modal-images">
                            {selectedProject.imagens.map((img, i) => (
                                <img key={i} src={img} alt={`${selectedProject.titulo} ${i + 1}`} />
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </section>
    );
};

export default Projetos;
